con:configuration.path:
declare namespace SOAP-ENV='http://schemas.xmlsoap.org/soap/envelope/';
declare namespace s='http://www.w3.org/2001/XMLSchema';
declare namespace ns1='http://tempuri.org';
//ns1:Home//ns1:Street

declare namespace SOAP-ENV='http://schemas.xmlsoap.org/soap/envelope/';
declare namespace s='http://www.w3.org/2001/XMLSchema';
declare namespace ns1='http://tempuri.org';

for $x in //ns1:Home
where $x//ns1:State = "NY"
return <zip>{data($x//ns1:Zip)}</zip>

con:configuration.scriptText:
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def holder = groovyUtils.getXmlHolder(messageExchange.responseContent)

holder.namespaces["ns1"] = "http://tempuri.org"

def homeZip = holder.getNodeValue("//ns1:FindPersonResult//ns1:Home//ns1:Zip")
def officeZip = holder.getNodeValue("//ns1:FindPersonResult//ns1:Office//ns1:Zip")
assert homeZip != officeZip

def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def holder = groovyUtils.getXmlHolder(messageExchange.responseContent)

holder.namespaces["ns1"] = "http://tempuri.org"

def ApiHomeSt = holder.getNodeValue("//ns1:Home//ns1:Street")
def ApiHomeCity = holder.getNodeValue("//ns1:Home//ns1:City")
def ApiHomeState = holder.getNodeValue("//ns1:Home//ns1:State")
def ApiHomeZip = holder.getNodeValue("//ns1:Home//ns1:Zip")

def ApiOfficeStreet = holder.getNodeValue("//ns1:Office//ns1:Street")
def ApiOfficeCity = holder.getNodeValue("//ns1:Office//ns1:City")
def ApiOfficeState = holder.getNodeValue("//ns1:Office//ns1:State")
def ApiOfficeZip = holder.getNodeValue("//ns1:Office//ns1:Zip")

if('${context.rowCount}'==1){
assert ApiHomeSt == context.expand('${Properties#street}')
assert ApiHomeCity == context.expand('${Properties#city}')
assert ApiHomeState == context.expand('${Properties#state}')
assert ApiHomeZip == context.expand('${Properties#zip}')
}

if('${context.rowCount}'==2){
assert ApiOfficeStreet == context.expand('${Properties#street}')
assert ApiOfficeCity == context.expand('${Properties#city}')
assert ApiOfficeState == context.expand('${Properties#state}')
assert ApiOfficeZip == context.expand('${Properties#zip}')
}

def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def holder = groovyUtils.getXmlHolder(messageExchange.responseContent)

holder.namespaces["ns1"] = "http://tempuri.org"

def ApiHomeStreet = holder.getNodeValue("//ns1:Home//ns1:Street")
assert ApiHomeStreet == context.expand('${Properties#HomeStreet}')

def ApiOfficeStreet = holder.getNodeValue("//ns1:Office//ns1:Street")
assert ApiOfficeStreet == context.expand('${Properties#OfficeStreet}')

def ApiHomeCity = holder.getNodeValue("//ns1:Home//ns1:City")
assert ApiHomeStreet == context.expand('${Properties#HomeCity}')

def ApiOfficeCity = holder.getNodeValue("//ns1:Office//ns1:City")
assert ApiOfficeCity == context.expand('${Properties#OfficeCity}')
